import React, { Component } from "react";
import "./navbar.css";
import { navigations } from "./navigations";
class Navbar extends Component {
  constructor() {
    super();
    this.state = {
      selected: "Popular",
    };
  }

  select = (m) => {
    this.setState({
        selected: m.name,
        mobile_menu:false
      });
      setTimeout(() => {
        this.props.selectCategory(m);
      }, 200);


  };
  render() {
    const { selected , mobile_menu} = this.state;
    return (
      <div className="navbar flex__start">
        <h3>MovieTime</h3>
        <button
        id={mobile_menu ? 'mobile__hamgurger--active' :''}
        className='mobile__hamgurger'
        onClick = {() => this.setState({
            mobile_menu:!mobile_menu
        })}
        >

        </button>
        <ul 
        id={mobile_menu ? 'navbar__categories--active' :''}
        className="navbar__categories flex__start">
          {navigations.map((m, i) => {
            return (
              <li
                style={{
                  opacity: selected === m.name ? 1 : "",
                }}
                onClick={() => this.select(m)}
              >
                {m.name}
              </li>
            );
          })}
        </ul>
      </div>
    );
  }
}

export default Navbar;
