import React, { Component } from 'react';
import Movie from "./Movie";
import InfiniteScroll from "react-infinite-scroller";
import MoviePreview from './parts/MoviePreview';



class MoviesList extends Component {
    constructor(){
        super()
        this.state = {

        }
    }

    handleMovieSelect = (row_id, ref) => {
      this.setState({
        selected: row_id,
      });
    };



    render() {
        const {stop, movies, selected} = this.props
        return (
            <section className="movies__list flex__start">
            <InfiniteScroll
              pageStart={0}
              loadMore={this.props.loadElements}
              hasMore={!stop}
              loader={
                <div className="loader" key={0}>
                  Loading ...
                </div>
              }
            //   getScrollParent={() => this.scrollParentRef}
              useWindow={false}
            >
              {movies && movies.length > 0
                ? movies.map((list, i) => {
                    return (
                      <ul
                        id={selected === i ? "selected__row" : ""}
                        className="movies__list__flex flex__center"
                      >
                        {list.map((m) => {
                          return (
                            <Movie
                            selected = {selected === i}
                              handleMovieSelect={() => this.handleMovieSelect(i)}
                              movie={m}
                            />
                          );
                        })}
                        {selected === i ? <MoviePreview /> : ""}
                      </ul>
                    );
                  })
                : ""}
            </InfiniteScroll>
          </section>
        );
    }
}

export default MoviesList;
